package com.sinoiov.yyzc.commons.mongodb.bean;import java.io.Serializable;import org.mongodb.morphia.annotations.Entity;import org.mongodb.morphia.annotations.Id;@Entity(value="clob_info", noClassnameStored=true)public class ClobBean implements Serializable{	private static final long serialVersionUID = 6228161449900096476L;		@Id	private String id;		private String time;		private String clob;	public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getTime() {		return time;	}	public void setTime(String time) {		this.time = time;	}	public String getClob() {		return clob;	}	public void setClob(String clob) {		this.clob = clob;	}}